import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Weddingcard.css';

function Weddingcard() {
  const [cardData, setCardData] = useState([]);
  const [editingCard, setEditingCard] = useState(null);
  const [newTitle, setNewTitle] = useState('');
  const [newImage, setNewImage] = useState(null); // For file upload
  const navigate = useNavigate();

  useEffect(() => {
    fetchCards();
  }, []);

  const fetchCards = () => {
    axios
      .get('http://localhost:3500/api/auth/getweddingdecor')
      .then((response) => {
        setCardData(response.data.services || []);
      })
      .catch((err) => {
        console.error('Error fetching data:', err);
        alert('Failed to fetch cards');
      });
  };

  const handleCardClick = (path) => {
    navigate(path);
  };

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:3500/api/auth/deleteweddingdecor/${id}`)
      .then(() => {
        setCardData((prevData) => prevData.filter((card) => card._id !== id));
        alert('Card deleted successfully');
      })
      .catch((error) => {
        console.error('Error deleting card:', error);
        alert('Failed to delete card');
      });
  };

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('http://localhost:3500/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      return response.data.imageUrl; // Assuming the API returns the uploaded image URL
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Failed to upload image');
      throw error;
    }
  };

  const handleEdit = async () => {
    if (!newTitle || !newImage) {
      alert('Please provide both a title and an image.');
      return;
    }

    try {
      const imageUrl = newImage instanceof File ? await handleFileUpload(newImage) : newImage;
      await axios.put(`http://localhost:3500/api/auth/updateweddingdecor/${editingCard._id}`, {
        title: newTitle,
        url: imageUrl,
      });
      setCardData((prevData) =>
        prevData.map((card) =>
          card._id === editingCard._id ? { ...card, title: newTitle, url: imageUrl } : card
        )
      );
      setEditingCard(null);
      setNewTitle('');
      setNewImage(null);
      alert('Card updated successfully');
    } catch (error) {
      console.error('Error updating card:', error);
      alert('Failed to update card');
    }
  };

  return (
    <div className="card-container">
      {cardData.map((card) => (
        <div key={card._id} className="card" style={{ cursor: 'pointer' }}>
          <img
            src={card.url}
            alt={card.title}
            className="card-image"
            onClick={() => handleCardClick(`/details/${card._id}`)}
          />
          <div className="card-content">
            <h3 className="card-title">{card.title}</h3>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => {
                setEditingCard(card);
                setNewTitle(card.title);
                setNewImage(card.url);
              }}
            >
              Update
            </button>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => handleDelete(card._id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
      {editingCard && (
        <div className="edit-form">
          <h3>Edit Card</h3>
          <label>
            Title:
            <input
              type="text"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </label>
          <label>
            Image:
            <input
              type="file"
              onChange={(e) => setNewImage(e.target.files[0])}
            />
          </label>
          <div className="wedding-btn">
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={handleEdit}
            >
              Update
            </button>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => {
                setEditingCard(null);
                setNewTitle('');
                setNewImage(null);
              }}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Weddingcard;
