import React, { useState,use } from 'react';
import { useNavigate } from 'react-router-dom';
import './Productcard.css';

const initialCardData = [
  {
    id: 1,
    image: "https://i.pinimg.com/originals/82/e2/b0/82e2b0a3d95b795237394e673a9a31e1.jpg",
    title: "Entrance Gate"
  },
  {
    id: 2,
    image: "https://thumbs.dreamstime.com/b/new-car-launch-beijing-auto-show-exhibition-booth-257773071.jpg",
    title: "car launch"
  },
  {
    id: 3,
    image: "https://i.pinimg.com/originals/eb/2e/52/eb2e52b49a189f6ab63f962b7425f7c4.jpg",
    title: "Party Hall"
  },
];

function Productcard() {
  const [cardData, setCardData] = useState(initialCardData);
  const [editingCard, setEditingCard] = useState(null); // Track card being edited
  const [newTitle, setNewTitle] = useState(''); // New/Edited title
  const [newImage, setNewImage] = useState(''); // New/Edited image URL
  const navigate = useNavigate();

  // Fetch all cards on component mount
  useEffect(() => {
    fetchCards();
  }, []);

  const fetchCards = () => {
    axios
      .get('http://localhost:3500/api/auth/getnamingceremony')
      .then((response) => {
        setCardData(response.data.services || []);
      })
      .catch((err) => {
        console.error('Error fetching data:', err);
        alert('Failed to fetch cards');
      });
  };

  // Navigate to card details page
  const handleCardClick = (path) => {
    navigate(path);
  };

  // Delete a card
  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:3500/api/auth/deletenamingceremony/${id}`)
      .then(() => {
        setCardData((prevData) => prevData.filter((card) => card._id !== id));
        alert('Card deleted successfully');
      })
      .catch((error) => {
        console.error('Error deleting card:', error);
        alert('Failed to delete card');
      });
  };

  // Start adding a new card
  const handleNew = () => {
    setEditingCard(null);
    setNewTitle('');
    setNewImage('');
  };

  // Add a new card (POST)
  const handleAdd = () => {
    if (!newTitle || !newImage) {
      alert('Please provide both a title and an image URL.');
      return;
    }

    axios
      .post('http://localhost:3500/api/auth/namingceremony', {
        title: newTitle,
        url: newImage,
      })
      .then((response) => {
        setCardData((prevData) => [...prevData, response.data]);
        setNewTitle('');
        setNewImage('');
        alert('Card added successfully');
      })
      .catch((error) => {
        console.error('Error adding card:', error);
        alert('Failed to add card');
      });
  };

  // Edit an existing card (PUT)
  const handleEdit = () => {
    if (!newTitle || !newImage) {
      alert('Please provide both a title and an image URL.');
      return;
    }

    axios
      .put(`http://localhost:3500/api/auth/updatenamingceremony/${editingCard._id}`, {
        title: newTitle,
        url: newImage,
      })
      .then(() => {
        setCardData((prevData) =>
          prevData.map((card) =>
            card._id === editingCard._id ? { ...card, title: newTitle, url: newImage } : card
          )
        );
        setEditingCard(null);
        setNewTitle('');
        setNewImage('');
        alert('Card updated successfully');
      })
      .catch((error) => {
        console.error('Error updating card:', error);
        alert('Failed to update card');
      });
  };

  return (
    <div className="card-container">

      {cardData.map((card) => (
        <div key={card._id} className="card" style={{ cursor: 'pointer' }}>
          <img
            src={card.url}
            alt={card.title}
            className="card-image"
            onClick={() => handleCardClick(`/details/${card._id}`)}
          />
          <div className="card-content">
            <h3 className="card-title">{card.title}</h3>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => {
                setEditingCard(card);
                setNewTitle(card.title);
                setNewImage(card.url);
              }}
            >
              Update
            </button>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => handleDelete(card._id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}

      {(editingCard || newTitle || newImage) && (
        <div className="edit-form">
          <h3>{editingCard ? 'Edit Card' : 'New Card'}</h3>
          <label>
            Title:
            <input
              type="text"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </label>
          <label>
            Image URL:
            <input
              type="text"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
            />
          </label>
          <div className="Naming-btn">
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={editingCard ? handleEdit : handleAdd}
            >
              {editingCard ? 'Update' : 'Add'}
            </button>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => {
                setEditingCard(null);
                setNewTitle('');
                setNewImage('');
              }}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Productcard;
