import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Birthdaycard.css';

function Birthdaycard() {
  const [cardData, setCardData] = useState([]); 
  const [editingCard, setEditingCard] = useState(null); 
  const [newTitle, setNewTitle] = useState(''); 
  const [newImage, setNewImage] = useState(''); 
  const navigate = useNavigate();

  useEffect(() => {
    fetchCards();
  }, []);

  // Fetch all cards
  const fetchCards = () => {
    axios
      .get('http://localhost:3500/api/auth/getbirthdayparties')
      .then((response) => {
        setCardData(response.data.services || []);
      })
      .catch((err) => {
        console.error('Error fetching data:', err);
        alert('Failed to fetch cards');
      });
  };

  // Navigate to card details page
  const handleCardClick = (path) => {
    navigate(path);
  };

  // Delete a card
  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:3500/api/auth/deletebirthday/${id}`)
      .then(() => {
        setCardData((prevData) => prevData.filter((card) => card._id !== id));
        alert('Card deleted successfully');
      })
      .catch((error) => {
        console.error('Error deleting card:', error);
        alert('Failed to delete card');
      });
  };

  // Reset form for adding a new card
  const handleNew = () => {
    setEditingCard(null);
    setNewTitle('');
    setNewImage('');
  };

  // Add a new card
  const handleAdd = () => {
    if (!newTitle || !newImage) {
      alert('Please provide both a title and an image URL.');
      return;
    }

    axios
      .post('http://localhost:3500/api/auth/weddingdecor', {
        title: newTitle,
        url: newImage,
      })
      .then((response) => {
        setCardData((prevData) => [...prevData, response.data]);
        setNewTitle('');
        setNewImage('');
        alert('Card added successfully');
      })
      .catch((error) => {
        console.error('Error adding card:', error);
        alert('Failed to add card');
      });
  };

  // Edit an existing card
  const handleEdit = () => {
    if (!newTitle || !newImage) {
      alert('Please provide both a title and an image URL.');
      return;
    }

    axios
      .put(`http://localhost:3500/api/auth/updatenamingceremony/${editingCard._id}`, {
        title: newTitle,
        url: newImage,
      })
      .then(() => {
        setCardData((prevData) =>
          prevData.map((card) =>
            card._id === editingCard._id ? { ...card, title: newTitle, url: newImage } : card
          )
        );
        setEditingCard(null);
        setNewTitle('');
        setNewImage('');
        alert('Card updated successfully');
      })
      .catch((error) => {
        console.error('Error updating card:', error);
        alert('Failed to update card');
      });
  };

  return (
    <div className="card-container">
      {/* Add New Card Button */}
      <button
        style={{ backgroundColor: 'lightblue', marginBottom: '20px', borderColor: 'white' }}
        onClick={handleNew}
      >
        Add New Card
      </button>

      {/* Display Cards */}
      {cardData.map((card) => (
        <div key={card._id} className="card" style={{ cursor: 'pointer' }}>
          <img
            src={card.url}
            alt={card.title}
            className="card-image"
            onClick={() => handleCardClick(`/details/${card._id}`)}
          />
          <div className="card-content">
            <h3 className="card-title">{card.title}</h3>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => {
                setEditingCard(card);
                setNewTitle(card.title);
                setNewImage(card.url);
              }}
            >
              Update
            </button>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={() => handleDelete(card._id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}

      {/* Form for Adding/Editing Cards */}
      {(editingCard || newTitle || newImage) && (
        <div className="edit-form">
          <h3>{editingCard ? 'Edit Card' : 'New Card'}</h3>
          <label>
            Title:
            <input
              type="text"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </label>
          <label>
            Image URL:
            <input
              type="text"
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
            />
          </label>
          <div className="wedding-btn">
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={editingCard ? handleEdit : handleAdd}
            >
              {editingCard ? 'Update' : 'Add'}
            </button>
            <button
              style={{ backgroundColor: 'lightgreen', marginRight: '10px', borderColor: 'white' }}
              onClick={handleNew}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Birthdaycard;
