import React, { useState } from "react";
import axios from 'axios';
import { useNavigate, Link } from "react-router-dom"; // Import Link for Solution 1
import './Register.css';

function Register() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const details = { username, password, email };
    
    axios.post('http://localhost:3000/api/auth', details)
      .then((res) => {
        console.log('Response:', res.data);
        alert('Form submitted successfully');
        setUsername('');
        setPassword('');
        setEmail('');
        setErrorMessage('');
      })
      .catch((err) => {
        console.error('Error:', err);
        if (err.response) {
          setErrorMessage(err.response.data.message || 'Server Error');
        } else if (err.request) {
          setErrorMessage('No response from server');
        } else {
          setErrorMessage('Error occurred while submitting');
        }
      });
  };

  const back = () => {
    navigate('/login');
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h2>Sign Up</h2>
        <div className="input-box">
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            required
          />
          <i className='bx bxs-user'></i>
        </div>
        <div className="input-box">
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          <i className='bx bxs-lock-alt'></i>
        </div>
        <div className="input-box">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
          />
        </div>

        

        
        <div className="remember-forgot">
          <label>
            <input type="checkbox" /> Remember me
          </label>
          <button
            type="button"
            className="link-button"
            onClick={() => alert("Forgot password functionality here")}
          >
            Forgot password
          </button>
        </div> 
       

        <button type="submit" className="btn">Register</button>
        <button type="button" onClick={back}>Login</button>
      </form>
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
    </div>
  );
}

export default Register;
