import React, { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Namingcard.css';

const initialCardData = [
  {
    id: 1,
    image: "https://th.bing.com/th/id/OIP.UOOfzDdCC_RNNHn4xY-7PwHaIl?rs=1&pid=ImgDetMain",
    title: "Entrance Gate"
  },
  {
    id: 2,
    image: "https://www.autumnstonephotography.co.uk/wp-content/uploads/2020/04/Indian-Hindu-Naming-Ceremony-Photography-Namkaran-Service-43.jpg",
    title: "Naming Decor"
  },
  {
    id: 3,
    image: "https://cdn.cherishx.com/uploads/1690280041_large.jpg",
    title: "Stage decor"
  },
];

function Namingcard() {
  const [cardData, setCardData] = useState(initialCardData);
  const [editingCard, setEditingCard] = useState(null);
  const [newTitle, setNewTitle] = useState('');
  const [newImage, setNewImage] = useState('');
  const navigate = useNavigate();

  useEffect(()=> {
    axios.get('http://localhost:3500/api/auth/getnamingceremony')
    .then(response =>{
      setCardData(response.data.services);
    })
    .catch(err =>{
       console.error('Error fetching ',err);
    })
   },[]);
  const handleCardClick = (path) => {
    navigate(path);
  };

  const handleDelete = (id) => {
    setCardData(cardData.filter((card) => card.id !== id));
  };

  const handleEdit = (card) => {
    setEditingCard(card);
    setNewTitle(card.title);
    setNewImage(card.image);
  };

  const handleUpdate = () => {
    setCardData(
      cardData.map((card) =>
        card.id === editingCard.id
          ? { ...card, title: newTitle, image: newImage }
          : card
      )
    );
    setEditingCard(null);
    setNewTitle('');
    setNewImage('');
  };

  return (
    <div className='card-container'>
      {cardData.map((card) => (
        <div
          key={card.id}
          className='card'
          style={{ cursor: 'pointer' }}
        >
          <img
            src={card.image}
            alt={card.title}
            className='card-image'
            onClick={() => handleCardClick(`/details/${card.id}`)} 
          />
          <div className="card-content">
            <h3 className='card-title'>{card.title}</h3>
            <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={() => handleEdit(card)}>Update</button>
            <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={() => handleDelete(card.id)}>Delete</button>
          </div>
        </div>
      ))}

      {editingCard && (
        <div className='edit-form'>
          <h3>Edit Card</h3>
          <label>
            Title:
            <input
              type='text'
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </label>
          <label>
            Image URL:
            <input
              type='text'
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
            />
          </label>
          <div className='Naming-btn'>
          <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={handleUpdate}>Update</button>
          <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={() => setEditingCard(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Namingcard;
