import React, { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Weddingcard.css';

const initialCardData = [
  {
    _id: 1,
    url: "https://th.bing.com/th/id/OIP.UOOfzDdCC_RNNHn4xY-7PwHaIl?rs=1&pid=ImgDetMain",
    title: "Entrance Gate"
  },
  {
    _id: 2,
    url: "https://i.pinimg.com/originals/cc/70/92/cc70928923e16871be6a2aac2f9835e5.jpg",
    title: "Reception Stage"
  },
  {
    _id: 3,
    url: "https://thumbs.dreamstime.com/z/weddings-vidhi-mandap-decoration-images-ac-banquet-hall-153509116.jpg",
    title: "Vidhi Mandap"
  },
];

function Weddingcard() {
  const [cardData, setCardData] = useState(initialCardData);
  const [editingCard, setEditingCard] = useState(null);
  const [newTitle, setNewTitle] = useState('');
  const [newImage, setNewImage] = useState('');
  const navigate = useNavigate();
 
  useEffect(()=> {
    axios.get('http://localhost:3500/api/auth/getweddingdecor')
    .then(response =>{
      setCardData(response.data.services);
    })
    .catch(err =>{
       console.error('Error fetching ',err);
    })
   },[]);
  

  const handleCardClick = (path) => {
    navigate(path);
  };
  
  const handleDelete = (id) => {
    axios.delete(`http://localhost:3500/api/auth/deleteweddingdecor/${id}`)
    .then(response =>{
        console.log("card deleted : ",response.data)
        setCardData(cardData.filter((card) => card._id !== id));
   })
   .catch(error =>{
    console.log('Error deleting user:',error);
   });
  };

  const handleNew = () => {
    setEditingCard({title:''});
    setNewTitle('');
    setNewImage('');
  };

  const handleUpdate = async () => {
    
    try {
      
      // Send POST request with form data
      const res = await axios.post('http://localhost:3500/api/auth/weddingdecor', {oldTitle:editingCard.title,title:newTitle,url:newImage});

      // If successful, log response and alert user
      console.log('Response:', res.data);
      alert('Form submitted successfully');
      axios.get('http://localhost:3500/api/auth/getweddingdecor')
    .then(response =>{
      setCardData(response.data.services);
      setEditingCard(null);
    })
    .catch(err =>{
       console.error('Error fetching ',err);
    });
    } catch (err) {
      // Handle different types of errors
      console.error('Error:', err);

      if (err.response) {
        // Server responded with an error status
        console.log(err.response.data.message || 'Server Error');
      } else if (err.request) {
        // No response from the server
        console.log('No response from server');
      } else {
        // Any other errors
        console.log('Error occurred while submitting');
      }
    }
    setCardData(
      cardData.map((card) =>
        card._id === editingCard._id
          ? { ...card, title: newTitle, url: newImage }
          : card
      )
    );
  };


  return (
    <div className='card-container'>
      {cardData.map((card) => (
        <div
          key={card._id}
          className='card'
          style={{ cursor: 'pointer' }}
        >
          <img
            src={card.url}
            alt={card.title}
            className='card-image'
            onClick={() => handleCardClick(`/details/${card._id}`)} 
          />
          <div className="card-content">
            <h3 className='card-title'>{card.title}</h3>
            <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={() => handleDelete(card._id)}>Delete</button>
          </div>
        </div>
      ))}
<button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white",height:"50px",width:"50px",marginTop:"10%"}} onClick={() => handleNew()}>+</button>
      {editingCard && (
        <div className='edit-form'>
          <h3>New Card</h3>
          <label>
            Title:
            <input
              type='text'
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </label>
          <label>
            Image URL:
            <input
              type='text'
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
            />
          </label>
          <label>
            Descriotion
          </label>
          <div className='wedding-btn'>
          <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={handleUpdate}>Add</button>
          <button style={{backgroundColor:"lightgreen",marginRight:"10px",borderColor:"white"}} onClick={() => setEditingCard(null)}>Cancel</button>
          
          </div>
        </div>
      )}
    </div>
  );
}

export default Weddingcard;
