import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './EventManagement.css';

const EventManagement = () => {
    const [count, setCount] = useState(0);
    const experienceRef = useRef(null);
    const [hasAnimated, setHasAnimated] = useState(false);
    const navigate = useNavigate(); // Hook for navigation

    // Navigate to Contact Us page on button click
    const handleContactClick = () => {
        navigate('/contact-us'); // This will navigate to the contact page
    };

    // Observe experience section to trigger the counter animation
    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                const entry = entries[0];
                if (entry.isIntersecting && !hasAnimated) {
                    animateCount();
                    setHasAnimated(true);
                }
            },
            { threshold: 0.5 }
        );

        if (experienceRef.current) {
            observer.observe(experienceRef.current);
        }

        return () => {
            if (experienceRef.current) {
                observer.unobserve(experienceRef.current);
            }
        };
    }, [hasAnimated]);

    // Counter animation function
    const animateCount = () => {
        let start = 1;
        const intervalId = setInterval(() => {
            setCount(start);
            if (start === 5) { // Assuming 5+ years of experience
                clearInterval(intervalId);
            }
            start += 1;
        }, 500);
    };

    return (
        <div className="event-management-container">
            <header className="hero-section">
                <h1>Event Management Services</h1>
                <p>Creating memorable experiences for your special occasions</p>
                <button className="cta-button" onClick={handleContactClick}>
                    Contact Us
                </button>
            </header>
            <div className="experience-section" ref={experienceRef}>
                <div className="experience-content">
                    <span className="experience-count">{count}+</span>
                    <span className="experience-text">Years of Experience</span>
                </div>
            </div>
        </div>
    );
};

export default EventManagement;
