import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
    const images =[
       "https://i.pinimg.com/originals/38/76/38/38763887c13ae78918a94681a7044f2e.jpg",
       "https://th.bing.com/th/id/R.2c3294dac319ea00669130f641ce3bb6?rik=5NitkGC35EXuNg&riu=http%3a%2f%2fwww.sayeridiary.com%2fwp-content%2fuploads%2f2018%2f08%2f72241d24-e14d-4cca-98a6-9af1d06b95cfrs_768.h.jpg&ehk=YQUZBXa108V0zQYtPKAYP873vJKprfatkZ7qvlRjiQ4%3d&risl=&pid=ImgRaw&r=0",
       "https://i.pinimg.com/originals/9c/a8/da/9ca8dafbcb5b070de00a456589be96ac.jpg",
       "https://cdn.encoreglobal.com/wp-content/uploads/sites/5/2022/11/09095139/Car-Launch-Event-Nissan_0007_MA424.jpg"
    ];
    const [currentImage, setCurrentImage] = useState(0);
    const [count, setCount] = useState(0);
    const experienceRef = useRef(null);
    const [hasAnimated, setHasAnimated] = useState(false);
    const [isServiceVisible, setIsServiceVisible] = useState(false); // State for dropdown visibility
    const navigate = useNavigate();

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImage((prevImage) => (prevImage+1) % images.length);
        },3000);
            
        return () => clearInterval(interval);
    },[images.length]);


    // Toggle visibility of the Services dropdown
    const toggleServices = () => {
        setIsServiceVisible(!isServiceVisible);
    };

    // Navigate to specific page
    const navigateTo = (path) => {
        navigate(path);
    };

    // Observe experience section to trigger counter animation
    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                const entry = entries[0];
                if (entry.isIntersecting && !hasAnimated) {
                    animateCount();
                    setHasAnimated(true);
                }
            },
            { threshold: 0.5 }
        );

        if (experienceRef.current) {
            observer.observe(experienceRef.current);
        }

        return () => {
            if (experienceRef.current) {
                observer.unobserve(experienceRef.current);
            }
        };
    }, [hasAnimated]);

    // Counter animation function
    const animateCount = () => {
        let start = 1;
        const intervalId = setInterval(() => {
            setCount(start);
            if (start === 5) {
                clearInterval(intervalId);
            }
            start += 1;
        }, 500);
    };

    return (
        <div className="home-container">
            {/* Navbar */}
            <nav className="navbar">
                <h2>Event Plus</h2>
                <ul className="navbar-list">
                    
                    <li onClick={() => navigateTo('/')}>Home</li>
                    <li className="services-menu" onClick={toggleServices}>
                        Services
                        {isServiceVisible && (
                            <ul className="dropdown">
                                <li onClick={() => navigateTo('/services/wedding-decors')}>Wedding Decors</li>
                                <li onClick={() => navigateTo('/services/wedding-management')}>Wedding Management</li>
                                <li onClick={() => navigateTo('/services/wedding-parties')}>Wedding Parties</li>
                                <li onClick={() => navigateTo('/services/product-launch')}>Product Launch</li>
                            </ul>
                        )}
                    </li>
                    <li onClick={() => navigateTo('/gallery')}>Gallery</li>
                    <li onClick={() => navigateTo('/about-us')}>About Us</li>
                    <li onClick={() => navigateTo('/contact-us')}>Contact Us</li>
                </ul>
            </nav>

            {/* Hero Section */}
            <header className="hero-section">
                <div className="hero-images">
                    {images.map((image,index) =>(
                    <img 
                    key={index}
                    src={image}
                    alt={`Slide ${index}`}
                    className={`image ${index === currentImage ? "active":""}`}
                    />
                    ))}
                
                <h1>India's Loading Event &  Wedding Management Company</h1>
                <p>Creating memorable experiences for your special occasions</p>
                <button className="cta-button">Let's talk - Send a message </button>
                </div>
            </header>
       {/* Experience Section */}
            <div className="experience-section" ref={experienceRef}>
                <div className="experience-left">
                    <p>Providing experience that makes you cherish your memory lane Lifelong.<br/>
                    Impactful & Transformational events that turn your success into money.
                    </p>
                    </div>
                    <div>
                    <div className="experience-right">
                    <span className="experience-years">
                        {count}
                        <span className="static-zero">0</span>
                        </span>
                        <span className='experience-text'>+ years of experience</span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;
