import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Weddingcard.css';

const initialCardData = [
  {
    id: 1,
    image: "https://th.bing.com/th/id/OIP.UOOfzDdCC_RNNHn4xY-7PwHaIl?rs=1&pid=ImgDetMain",
    title: "Entrance Gate"
  },
  {
    id: 2,
    image: "https://i.pinimg.com/originals/cc/70/92/cc70928923e16871be6a2aac2f9835e5.jpg",
    title: "Reception Stage"
  },
  {
    id: 3,
    image: "https://thumbs.dreamstime.com/z/weddings-vidhi-mandap-decoration-images-ac-banquet-hall-153509116.jpg",
    title: "Vidhi Mandap"
  },
];

function Weddingcard() {
  const [cardData, setCardData] = useState(initialCardData);
  const [editingCard, setEditingCard] = useState(null);
  const [newTitle, setNewTitle] = useState('');
  const [newImage, setNewImage] = useState('');
  const navigate = useNavigate();

  const handleCardClick = (path) => {
    navigate(path);
  };

  const handleDelete = (id) => {
    setCardData(cardData.filter((card) => card.id !== id));
  };

  const handleEdit = (card) => {
    setEditingCard(card);
    setNewTitle(card.title);
    setNewImage(card.image);
  };

  const handleUpdate = () => {
    setCardData(
      cardData.map((card) =>
        card.id === editingCard.id
          ? { ...card, title: newTitle, image: newImage }
          : card
      )
    );
    setEditingCard(null);
    setNewTitle('');
    setNewImage('');
  };

  return (
    <div className='card-container'>
      {cardData.map((card) => (
        <div
          key={card.id}
          className='card'
          style={{ cursor: 'pointer' }}
        >
          <img
            src={card.image}
            alt={card.title}
            className='card-image'
            onClick={() => handleCardClick(`/details/${card.id}`)} 
          />
          <div className="card-content">
            <h3 className='card-title'>{card.title}</h3>
            <button style={{backgroundColor:"lightgreen",mar}} onClick={() => handleEdit(card)}>Update</button>
            <button onClick={() => handleDelete(card.id)}>Delete</button>
          </div>
        </div>
      ))}

      {editingCard && (
        <div className='edit-form'>
          <h3>Edit Card</h3>
          <label>
            Title:
            <input
              type='text'
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </label>
          <label>
            Image URL:
            <input
              type='text'
              value={newImage}
              onChange={(e) => setNewImage(e.target.value)}
            />
          </label>
          <div className='wedding-btn'>
          <button onClick={handleUpdate}>Update</button>
          <button onClick={() => setEditingCard(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Weddingcard;
