import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const EventManagement = () => {
    const [count, setCount] = useState(0);
    const experienceRef = useRef(null);
    const [hasAnimated, setHasAnimated] = useState(false);
    const [isServiceVisible, setIsServiceVisible] = useState(false); // State for dropdown visibility
    const navigate = useNavigate();

    // Toggle visibility of the Services dropdown
    const toggleServices = () => {
        setIsServiceVisible(!isServiceVisible);
    };

    // Navigate to specific page
    const navigateTo = (path) => {
        navigate(path);
    };

    // Observe experience section to trigger counter animation
    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                const entry = entries[0];
                if (entry.isIntersecting && !hasAnimated) {
                    animateCount();
                    setHasAnimated(true);
                }
            },
            { threshold: 0.5 }
        );

        if (experienceRef.current) {
            observer.observe(experienceRef.current);
        }

        return () => {
            if (experienceRef.current) {
                observer.unobserve(experienceRef.current);
            }
        };
    }, [hasAnimated]);

    // Counter animation function
    const animateCount = () => {
        let start = 1;
        const intervalId = setInterval(() => {
            setCount(start);
            if (start === 5) {
                clearInterval(intervalId);
            }
            start += 1;
        }, 500);
    };

    return (
        <div className="event-management-container">
            {/* Navbar */}
            <nav className="navbar">
                <ul className="navbar-list">
                    <li onClick={() => navigateTo('/')}>Home</li>
                    <li className="services-menu" onClick={toggleServices}>
                        Services
                        {isServiceVisible && (
                            <ul className="dropdown">
                                <li onClick={() => navigateTo('/services/wedding-decors')}>Wedding Decors</li>
                                <li onClick={() => navigateTo('/services/wedding-management')}>Wedding Management</li>
                                <li onClick={() => navigateTo('/services/wedding-parties')}>Wedding Parties</li>
                                <li onClick={() => navigateTo('/services/product-launch')}>Product Launch</li>
                            </ul>
                        )}
                    </li>
                    <li onClick={() => navigateTo('/gallery')}>Gallery</li>
                    <li onClick={() => navigateTo('/about-us')}>About Us</li>
                    <li onClick={() => navigateTo('/contact-us')}>Contact Us</li>
                </ul>
            </nav>

            {/* Hero Section */}
            <header className="hero-section">
                <h1>Event Management Services</h1>
                <p>Creating memorable experiences for your special occasions</p>
                <button className="cta-button" onClick={() => navigateTo('/contact-us')}>
                    Contact Us
                </button>
            </header>

            {/* Experience Section */}
            <div className="experience-section" ref={experienceRef}>
                <div className="experience-content">
                    <span className="experience-count">{count}+</span>
                    <span className="experience-text">Years of Experience</span>
                </div>
            </div>
        </div>
    );
};

export default Home;
