import React, { useEffect, useState, } from 'react';
import { useNavigate } from 'react-router-dom';
import './weddingDecor.css';

const WeddingDecor = () => {
    const images = [
        "https://i.pinimg.com/originals/38/76/38/38763887c13ae78918a94681a7044f2e.jpg",
        "https://balloonpro.in/wp-content/uploads/2023/06/20210306_092227.jpg",
        "https://thequick-witted.com/wp-content/uploads/2020/10/birthday-party-halls-for-rent-lovely-interior-design-banquet-hall-of-birthday-party-halls-for-rent.jpg",
        "https://cdn.encoreglobal.com/wp-content/uploads/sites/5/2022/11/09095139/Car-Launch-Event-Nissan_0007_MA424.jpg"
    ];
    const [currentImage, setCurrentImage] = useState(0);
    const [isServiceVisible, setIsServiceVisible] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImage((prevImage) => (prevImage + 1) % images.length);
        }, 3000);

        return () => clearInterval(interval);
    }, [images.length]);

    const navigateTo = (path) => {
        navigate(path);
    };

    const toggleServices = () => {
        setIsServiceVisible(!isServiceVisible);
    };

    return (
        <div className="weddingDecor-container">
            {/* Navbar */}
            <nav className="navbar">
                <h2>Event Plus</h2>
                <ul className="navbar-list">
                    <li onClick={() => navigateTo('/')}>Home</li>
                    <li className="services-menu" onClick={toggleServices}>
                        Services
                        {isServiceVisible && (
                            <ul className="dropdown">
                                <li onClick={() => navigateTo('/services/wedding-decors')}>Wedding Decors</li>
                                <li onClick={() => navigateTo('/services/Naming-ceremony')}>Naming Ceremony</li>
                                <li onClick={() => navigateTo('/services/Birthday-parties')}>Birthday Parties</li>
                                <li onClick={() => navigateTo('/services/product-launch')}>Product Launch</li>
                            </ul>
                        )}
                    </li>
                    <li onClick={() => navigateTo('/about-us')}>About Us</li>
                    <li onClick={() => navigateTo('/contact-us')}>Contact Us</li>
                </ul>
            </nav>

            {/* Hero Section */}
            <header className="section1">
                <div className="hero-images1">
                    {images.map((image, index) => (
                        <img
                            key={index}
                            src={image}
                            alt={`Slide ${index}`}
                            className={`image ${index === currentImage ? "active" : ""}`}
                        />
                    ))}
                </div>
            </header>
            <div>
                <h6>Service</h6>
                <h1>WeddingDecor</h1>
                <p>Event Plus Management Pvt. Ltd. – is Mumbai based Event Planning & Wedding décor company.

                    Having officially started the journey 5 years ago, we may sound young but our team comprises young members who each bring to the table ,an experience close to a decade in consumer services, events and marketing.

                    We transfer your special occasion into an unforgettable experience</p>
            </div>
        </div>
    );
};

export default WeddingDecor;
