import React, { useState } from "react";
import axios from 'axios';
import { useNavigate, Link } from "react-router-dom";
import './Register.css';

function Register() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password.length < 6) {
      setErrorMessage("Password must be at least 6 characters long");
      return;
    }

    const details = { username, password, email };
    setLoading(true);
    setErrorMessage('');

    try {
      const res = await axios.post('http://localhost:3500/api/auth/register', details);
      console.log('Response:', res.data);
      alert('Form submitted successfully');
      setUsername('');
      setEmail('');
      setPassword('');
      setLoading(false);
      setErrorMessage('');
      navigate('/login');
    } catch (err) {
      console.error('Error:', err);
      setLoading(false);
      if (err.response) {
        setErrorMessage(err.response.data.message || 'Server Error');
      } else if (err.request) {
        setErrorMessage('No response from server');
      } else {
        setErrorMessage('Error occurred while submitting');
      }
    }
  };

  return (
    <div className="register-container">
      <form onSubmit={handleSubmit} className="register-form">
        <h2>Sign Up</h2>
        <div className="mb-3">
          <label htmlFor="username" className="form-label">Username</label>
          <input
            type="text"
            id="username"
            className="form-control"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your username"
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            id="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            id="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
            required
          />
        </div>
        <div className="form-check mb-3">
          <input type="checkbox" className="form-check-input" id="rememberMe" />
          <label className="form-check-label" htmlFor="rememberMe">Remember me</label>
        </div>
        <button type="submit" className="btn btn-primary mb-3" disabled={loading}>
          {loading ? 'Registering...' : 'Register'}
        </button>
        <Link to="/login" className="btn btn-secondary">Login</Link>
        {errorMessage && <p className="error-message mt-3">{errorMessage}</p>}
      </form>
    </div>
  );
}

export default Register;
