import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Navbar = ({ toggleServices, isServiceVisible }) => (
    <nav className='navbar'>
        <h1 style={{ color: 'red' }}>EventPlanner</h1>
        <ul>
            <li>Home</li>
            <li>About us</li>
            <li className="services-menu" onClick={toggleServices}>
                Services
                {isServiceVisible && (
                    <div className='services-list'>
                        <ul>
                            <li>Wedding Decors</li>
                            <li>Wedding Management</li>
                            <li>Wedding parties</li>
                            <li>Product Launch</li>
                        </ul>
                    </div>
                )}
            </li>
            <li>Work Gallery</li>
            <li>Contact us</li>
        </ul>
    </nav>
);

const HeroSection = ({ images, currentImage }) => (
    <header className="hero-section">
        <div className="hero-images">
            {images.map((image, index) => (
                <img
                    key={index}
                    src={image}
                    alt={`Slide ${index}`}
                    className={`image ${index === currentImage ? "active" : ""}`}
                />
            ))}
        </div>
        <h2>India's leading Event & Wedding Company</h2>
        <button className='cta-button'>Let's talk - Send a message</button>
    </header>
);

const ExperienceSection = ({ count, experienceRef }) => (
    <div className='experience-section' ref={experienceRef}>
        <div className="experience-left">
            <p>Providing experiences that make you cherish your memory lane lifelong.
                <br />
                Impact & Transformational events that turn your success into money.</p>
        </div>
        <div className="experience-right">
            <span className='experience-years'>
                {count}
                <span className='static-zero'>0</span>
            </span>
            <span className='experience-text'>+ Years of experience</span>
        </div>
    </div>
);

function Home() {
    const images = [
        "https://i.pinimg.com/originals/cf/8e/98/cf8e98d0c788355fd51e35b10be0de48.jpg",
    ];

    const [currentImage, setCurrentImage] = useState(0);
    const [count, setCount] = useState(0);
    const experienceRef = useRef(null);
    const [hasAnimated, setHasAnimated] = useState(false);
    const [isServiceVisible, setIsServiceVisible] = useState(false);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImage((prevImage) => (prevImage + 1) % images.length);
        }, 3000);

        return () => clearInterval(interval);
    }, [images.length]);

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                const entry = entries[0];
                if (entry.isIntersecting && !hasAnimated) {
                    animateCount();
                    setHasAnimated(true);
                }
            },
            { threshold: 0.5 }
        );

        if (experienceRef.current) {
            observer.observe(experienceRef.current);
        }

        return () => {
            if (experienceRef.current) {
                observer.unobserve(experienceRef.current);
            }
        };
    }, [hasAnimated]);

    const animateCount = () => {
        let start = 1;
        const interval = setInterval(() => {
            setCount(start);
            if (start === 4) {
                clearInterval(interval);
            }
            start += 1;
        }, 500);
    };

    const toggleServices = () => {
        setIsServiceVisible(!isServiceVisible);
    };

    return (
        <div className='home-container'>
            <Navbar toggleServices={toggleServices} isServiceVisible={isServiceVisible} />
            <HeroSection images={images} currentImage={currentImage} />
            <ExperienceSection count={count} experienceRef={experienceRef} />
        </div>
    );
}

export default Home;
