import React, { useState } from 'react';
import { login } from './authService';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const LoginForm = () => {
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log({ email, password });
      const { data } = await login({ email, password });
      console.log(data);
      localStorage.setItem('token', data.token);
      navigate('/home');
    } catch (err) {
      console.error('Login failed', err);
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin} className="p-4 shadow-lg bg-white rounded">
        <h2>Login</h2>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <div className="input-group">
            <span className="input-group-text">
              <img
                src="https://static.vecteezy.com/system/resources/previews/000/581/999/original/email-icon-vector-illustration.jpg"
                alt="Email Icon"
                style={{ width: '20px', height: '20px' }}
              />
            </span>
            <input
              type="email"
              id="email"
              className="form-control"
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <div className="input-group">
            <span className="input-group-text">
              <img
                src="https://cdn4.iconfinder.com/data/icons/eldorado-basic/40/unlock-512.png"
                alt="Password Icon"
                style={{ width: '20px', height: '20px' }}
              />
            </span>
            <input
              type="password"
              id="password"
              className="form-control"
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
