import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Card from './Card';
import Footer from './Footer';

const Home = () => {
    const images = [
        "https://i.pinimg.com/originals/38/76/38/38763887c13ae78918a94681a7044f2e.jpg",
        "https://balloonpro.in/wp-content/uploads/2023/06/20210306_092227.jpg",
        "https://thequick-witted.com/wp-content/uploads/2020/10/birthday-party-halls-for-rent-lovely-interior-design-banquet-hall-of-birthday-party-halls-for-rent.jpg",
        "https://cdn.encoreglobal.com/wp-content/uploads/sites/5/2022/11/09095139/Car-Launch-Event-Nissan_0007_MA424.jpg"
    ];
    const [currentImage, setCurrentImage] = useState(0);
    const experienceRef = useRef(null);
    const [isServiceVisible, setIsServiceVisible] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImage((prevImage) => (prevImage + 1) % images.length);
        }, 3000);

        return () => clearInterval(interval);
    }, [images.length]);

    const toggleServices = () => {
        setIsServiceVisible((prev) => !prev);
    };

    const navigateTo = (path) => {
        navigate(path);
    };

    return (
        <div className="container-fluid">
            {/* Navbar */}
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <h2 className="navbar-brand">Event Plus</h2>
                    <button
                        className="navbar-toggler"
                        type="button"
                        data-bs-toggle="collapse"
                        data-bs-target="#navbarNav"
                        aria-controls="navbarNav"
                        aria-expanded="false"
                        aria-label="Toggle navigation"
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav ms-auto">
                            <li className="nav-item">
                                <span className="nav-link" onClick={() => navigateTo('/Home')}>Home</span>
                            </li>
                            <li className="nav-item dropdown">
                                <span
                                    className="nav-link dropdown-toggle"
                                    role="button"
                                    onClick={toggleServices}
                                >
                                    Services
                                </span>
                                {isServiceVisible && (
                                    <ul className="dropdown-menu">
                                        <li className="dropdown-item" onClick={() => navigateTo('/WeddingDecor')}>Wedding Decors</li>
                                        <li className="dropdown-item" onClick={() => navigateTo('/NamingCeremony')}>Naming Ceremony</li>
                                        <li className="dropdown-item" onClick={() => navigateTo('/BirthdayParties')}>Birthday Parties</li>
                                        <li className="dropdown-item" onClick={() => navigateTo('/ProductLaunch')}>Product Launch</li>
                                    </ul>
                                )}
                            </li>
                            <li className="nav-item">
                                <span className="nav-link" onClick={() => navigateTo('/About')}>About Us</span>
                            </li>
                            <li className="nav-item">
                                <span className="nav-link" onClick={() => navigateTo('/Contact')}>Contact Us</span>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>

            {/* Hero Section */}
            <header className="position-relative text-center text-white">
                <div id="carouselExampleFade" className="carousel slide carousel-fade" data-bs-ride="carousel">
                    <div className="carousel-inner">
                        {images.map((image, index) => (
                            <div
                                key={index}
                                className={`carousel-item ${index === currentImage ? "active" : ""}`}
                            >
                                <img src={image} className="d-block w-100" alt={`Slide ${index}`} />
                            </div>
                        ))}
                    </div>
                </div>
                <div className="carousel-caption d-none d-md-block">
                    <h1 className="display-4 fw-bold">India's Leading Event & Wedding Management Company</h1>
                    <p className="lead">Creating memorable experiences for your special occasions</p>
                    <button className="btn btn-primary btn-lg">Let's Talk - Send a Message</button>
                </div>
            </header>

            {/* Experience Section */}
            <div className="row my-5" ref={experienceRef}>
                <div className="col-md-6 d-flex align-items-center">
                    <p className="fs-5">
                        Providing experiences that make you cherish memories for a lifetime.<br />
                        Impactful & transformational events that turn your success into unforgettable moments.
                    </p>
                </div>
                <div className="col-md-6 text-center">
                    <span className="display-1 fw-bold text-primary">50+</span>
                    <p className="fs-4">Years of Experience</p>
                </div>
            </div>

            {/* Card Component */}
            <Card />

            {/* Footer */}
            <Footer />
        </div>
    );
};

export default Home;
