// src/Home.js
import React, { useEffect, useState } from 'react';
import { getHome } from './authService';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) return navigate('/login');

      try {
        const { data } = await getHome(token);
        setMessage(data.message);
      } catch (err) {
        console.error('Invalid token', err);
        navigate('/login');
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="home-container">
      <header>
        <h1>Event Management</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </header>

      <div className="hero">
        <h1>{message || "Bringing Your Events to Life"}</h1>
        <p>From weddings to corporate events, we handle every detail to make your vision a reality.</p>
        <button className="cta-button" onClick={() => navigate('/services')}>Explore Our Services</button>
      </div>

      <section id="about">
        <h2>About Us</h2>
        <p>With years of experience in event planning, we make every occasion unforgettable.</p>
      </section>

      <section id="services">
        <h2>Our Services</h2>
        <ul>
          <li>Wedding Planning</li>
          <li>Corporate Events</li>
          <li>Parties and Celebrations</li>
          <li>Custom Event Design</li>
        </ul>
      </section>

      <section id="portfolio">
        <h2>Portfolio</h2>
        <p>Check out some of our previous work and the memorable moments weâ€™ve helped create.</p>
        
      </section>

      <section id="testimonials">
        <h2>Testimonials</h2>
        <blockquote>"Best event planning service! Our wedding was a dream come true." - Happy Client</blockquote>
      </section>

      <section id="contact">
        <h2>Contact Us</h2>
        <p>Ready to plan your next event? Reach out to us for a free quote!</p>
        <button onClick={() => navigate('/contact')} className="cta-button">Contact Us</button>
      </section>

      <footer>
        <p>&copy; 2024 Event Management. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
