import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './About.css';
import Footer from './Footer';

const About = () => {
    const navigate = useNavigate();
    const [isServiceVisible, setIsServiceVisible] = useState(false);
    const [events, setEvents] = useState([]); // State for events from the backend
    const [newEvent, setNewEvent] = useState({ name: '', description: '' }); // State for new event data

    const navigateTo = (path) => {
        navigate(path);
    };

    const toggleServices = () => {
        setIsServiceVisible((prevVisible) => !prevVisible);
    };

    // Fetch events from the backend
    useEffect(() => {
        fetch('http://localhost:5000/api/events') // Backend GET endpoint
            .then((response) => response.json())
            .then((data) => setEvents(data))
            .catch((error) => console.error('Error fetching events:', error));
    }, []);

    // Handle form submission for adding a new event
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:5000/api/events', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newEvent), // Send newEvent data to backend
        })
            .then((response) => response.json())
            .then((data) => {
                console.log('Event added:', data);
                setEvents((prevEvents) => [...prevEvents, data]); // Update UI with new event
                setNewEvent({ name: '', description: '' }); // Clear form inputs
            })
            .catch((error) => console.error('Error adding event:', error));
    };

    return (
        <div className="home-container">
            {/* Navbar */}
            <nav className="navbar">
                <h2>Event Plus</h2>
                <ul className="navbar-list">
                    <li onClick={() => navigateTo('/home')}>Home</li>
                    <li className="services-menu" onClick={toggleServices}>
                        Services
                        {isServiceVisible && (
                            <ul className="dropdown">
                                <li onClick={() => navigateTo('/WeddingDecor')}>Wedding Decors</li>
                                <li onClick={() => navigateTo('/NamingCeremony')}>Naming Ceremony</li>
                                <li onClick={() => navigateTo('/BirthdayParties')}>Birthday Parties</li>
                                <li onClick={() => navigateTo('/ProductLaunch')}>Product Launch</li>
                            </ul>
                        )}
                    </li>
                    <li onClick={() => navigateTo('/about')}>About Us</li>
                    <li onClick={() => navigateTo('/Contact')}>Contact Us</li>
                </ul>
            </nav>

            {/* About Content */}
            <div className="About-content">
                <h1>About Us</h1>
                <p>We are one of the leading Event Management companies...</p>
            </div>

            {/* Event List */}
            <div>
                <h2>Our Events</h2>
                {events.length > 0 ? (
                    <ul>
                        {events.map((event) => (
                            <li key={event._id}>
                                <h3>{event.name}</h3>
                                <p>{event.description}</p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No events found. Add one below!</p>
                )}
            </div>

            {/* Add Event Form */}
            <div>
                <h2>Add a New Event</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Event Name"
                        value={newEvent.name}
                        onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
                        required
                    />
                    <textarea
                        placeholder="Event Description"
                        value={newEvent.description}
                        onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
                        required
                    />
                    <button type="submit">Add Event</button>
                </form>
            </div>

            {/* Footer */}
            <Footer />
        </div>
    );
};

export default About;
