import React, { useEffect, useState, useRef } from 'react'
import { getHome } from './authService';
import { useNavigate } from 'react-router-dom';
import './Home.css';
function Home() {
    const images = [
        "https://i.pinimg.com/originals/cf/8e/98/cf8e98d0c788355fd51e35b10be0de48.jpg",
    ]

    const [currentImage, setCurrentImage] = useState(0);
    const [count, setCount] = useState(0);
    const experienceRef = useRef(null);
    const [hasAnimated, setAnimated] = useState(false);
    const [isServiceVisible, setServiceVisible] = useState(false);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImage((prevImage) => (prevImage + 1) % images.length);
        }, 3000);

        return () => clearInterval(interval);
    }, [images.length]);

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                const entry = entries[0];
                if (entry.isIntersecting && !hasAnimated) {
                    animateCount();
                    setHasAnimated(true);
                }
            },
            { threshold: 0.5 }
        );
        if (experienceRef.current) {
            observer.observe(experienceRef.current);
        }
        return () => {
            if (experienceRef.current) {
                observer.unobserve(experienceoRef.current);
            }
        };

    }, [hasAnimated]);
    const animateCount = () => {
        let start = 1;
        const interval = setInterval(() => {
            setCount(start);
            if (start === 4) {
                clearInterval(interval);
            }
            start += 1;
        }, 500);
    };
    const toggleServices = () => {
        setIsServicesVisible(!isServiceVisible);
    };

    return (
        <div className='home-container'>
            <nav className='navbar'>
                <h1 style={{ color: 'red' }}>EventPlanner</h1>
                <ul>
                    <li>Home</li>
                    <li>About us</li>
                    <li className="services-menu" onClick={toggleServices}>
                        Services
                        {isServiceVisible && (
                            <div className='services-list'>
                                <ul>
                                    <li>Wedding Decors</li>
                                    <li>Wedding Management</li>
                                    <li>Wedding parties</li>
                                    <li>Product Lunch</li>
                                </ul>
                            </div>
                        )}
                    </li>
                    <li>Work Gallery</li>
                    <li>Contact us</li>
                </ul>
            </nav>
            <header className="hero-section">
                <div className="hero-images">
                    {images.map((image, index) => (
                        <img
                            key={index}
                            src={image}
                            alt={`Slide ${index}`}
                            className={`image $(index === currebtImage ? "active ": "")`}
                        />
                    ))}
                </div>
                <h2>India's leading Event & Wedding Company</h2>
                <button className='cta-button'>Let;s talk-Send a message</button>
            </header>

            <div className='experience-section' ref={experienceRef}>
                <div className="experience-left">
                    <p>Providing experience that makes you
                        cherish your memory lane lifelong.
                        <br/> 
                        Impact & Transformational events that turn your
                        success into money.</p>
                </div>
            </div>
        </div>
    )
}

export default Home
